mapper.select:
select A.*, count(1) over() as CNT, ceil(count(1) over()/#{pagesize}) as PAGE from( select u.userid, u.username, u.userphone, u.userphoto, u.userbirthday, u.sex, u.address, u.invitecode, u.credit, u.coin, v.venuename, u.employeetype, u.teachertype, u.employeestatus, r.rolename from sys_user u join sys_user_role ur on ur.userid = u.userid left join sys_role r on r.roleid = ur.roleid and r.flag = '0' left join yj_venue v on v.venueid = u.venueid and v.flag = '0' where u.flag = '0'
and username like concat('%',#{username},'%')
and ur.roleid = #{roleid}
and u.venueid = #{venueid}
order by u.createdate desc )A limit #{begin},#{end}

select A.*, count(1) over() as CNT, ceil(count(1) over()/#{pagesize}) as PAGE from( select u.userid, u.username, u.userphone, u.userphoto, u.userbirthday, u.sex, u.address, u.invitecode, u.credit, u.coin, v.venuename, u.employeetype, u.teachertype, u.employeestatus, r.rolename from sys_user u join sys_user_role ur on ur.userid = u.userid and (ur.roleid = 'c71e232b87a74dafa8b216deafffb969' or ur.roleid = '2b266670fefa4e06b86cebc682a83237') left join sys_role r on r.roleid = ur.roleid and r.flag = '0' left join yj_venue v on v.venueid = u.venueid and v.flag = '0' where u.flag = '0'
and username like concat('%',#{username},'%')
and u.venueid = #{venueid}
and u.employeestatus = #{employeestatus}
order by u.createdate desc )A limit #{begin},#{end}
